// GymCourtReport.js
import React, { useState, useEffect } from 'react';
import dayjs from 'dayjs';
import 'dayjs/locale/he';
dayjs.locale('he');

const SHEET_BASE = "https://opensheet.elk.sh/1x1e53gsx3VzEJ5ON5QOvYeJTGI8auMtSxJYYpUUmreY";

const formatDate = (timestamp) => dayjs(timestamp, 'DD/MM/YYYY HH:mm:ss').format('YYYY-MM-DD');

function processData(data, fieldName) {
  const byDate = {}; // ◊ß◊ë◊ï◊¶◊™◊ô ◊ú◊§◊ô ◊ô◊ï◊ù
  const byPlayer = {}; // ◊ê◊ô◊©◊ô ◊ú◊§◊ô ◊©◊ó◊ß◊†◊ô◊™

  data.forEach(row => {
    const date = formatDate(row.Timestamp);
    const name = row.Name;
    const value = Number(row[fieldName]);
    if (!value || !name || isNaN(value)) return;

    if (!byDate[date]) byDate[date] = [];
    byDate[date].push(value);

    if (!byPlayer[name]) byPlayer[name] = {};
    if (!byPlayer[name][date]) byPlayer[name][date] = [];
    byPlayer[name][date].push(value);
  });

  return { byDate, byPlayer };
}

function calculateDailyAvg(byDate) {
  return Object.entries(byDate).map(([date, values]) => ({
    date,
    avg: (values.reduce((a, b) => a + b, 0) / values.length).toFixed(1)
  })).sort((a, b) => new Date(a.date) - new Date(b.date));
}

function rollingAverage(daily, daysBack) {
  const recent = daily.slice(-daysBack);
  const values = recent.map(d => Number(d.avg)).filter(n => !isNaN(n));
  return values.length ? (values.reduce((a, b) => a + b, 0) / values.length).toFixed(1) : '◊ê◊ô◊ü ◊†◊™◊ï◊†◊ô◊ù';
}

function averageInRange(data, from, to) {
  const filtered = data.filter(d => d.date >= from && d.date <= to);
  const values = filtered.map(d => Number(d.avg)).filter(n => !isNaN(n));
  return values.length ? (values.reduce((a, b) => a + b, 0) / values.length).toFixed(1) : '◊ê◊ô◊ü ◊†◊™◊ï◊†◊ô◊ù';
}

function RPEReport({ title, tabName, fieldName }) {
  const [raw, setRaw] = useState([]);
  const [data, setData] = useState({ byDate: {}, byPlayer: {} });
  const [selectedPlayer, setSelectedPlayer] = useState(null);
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [customAvg, setCustomAvg] = useState(null);

  useEffect(() => {
    fetch(`${SHEET_BASE}/${tabName}`)
      .then(res => res.json())
      .then(json => {
        setRaw(json);
        setData(processData(json, fieldName));
      });
  }, [tabName, fieldName]);

  const dailyAvg = calculateDailyAvg(data.byDate);
  const playerNames = Object.keys(data.byPlayer);

  return (
    <div style={{ marginBottom: '40px' }}>
      <h2>{title}</h2>

      <h3>üìÖ ◊û◊û◊ï◊¶◊¢◊ô◊ù ◊ú◊§◊ô ◊ô◊ï◊ù (8 ◊ô◊û◊ô◊ù ◊ê◊ó◊®◊ï◊†◊ô◊ù)</h3>
      <table border="1" cellPadding="6">
        <thead><tr><th>◊™◊ê◊®◊ô◊ö</th><th>◊û◊û◊ï◊¶◊¢ ◊ß◊ë◊ï◊¶◊™◊ô</th></tr></thead>
        <tbody>
          {dailyAvg.slice(-8).map((entry, i) => (
            <tr key={i}><td>{entry.date}</td><td>{entry.avg}</td></tr>
          ))}
        </tbody>
      </table>

      <h3>üìà ◊û◊û◊ï◊¶◊¢ ◊û◊™◊í◊ú◊í◊ú</h3>
      <p>3 ◊ô◊û◊ô◊ù: {rollingAverage(dailyAvg, 3)} | 5 ◊ô◊û◊ô◊ù: {rollingAverage(dailyAvg, 5)} | 8 ◊ô◊û◊ô◊ù: {rollingAverage(dailyAvg, 8)}</p>

      <h3>üìÜ ◊û◊û◊ï◊¶◊¢ ◊ú◊§◊ô ◊ò◊ï◊ï◊ó ◊™◊ê◊®◊ô◊õ◊ô◊ù</h3>
      <input type="date" value={from} onChange={e => setFrom(e.target.value)} />
      <input type="date" value={to} onChange={e => setTo(e.target.value)} />
      <button onClick={() => setCustomAvg(averageInRange(dailyAvg, from, to))}>◊ó◊©◊ë ◊û◊û◊ï◊¶◊¢</button>
      {customAvg && <p>◊û◊û◊ï◊¶◊¢ ◊ë◊ò◊ï◊ï◊ó: {customAvg}</p>}

      <h3>üë§ ◊û◊û◊ï◊¶◊¢◊ô◊ù ◊ú◊§◊ô ◊©◊ó◊ß◊†◊ô◊™</h3>
      {playerNames.map(name => {
        const playerData = calculateDailyAvg(data.byPlayer[name] || {});
        return (
          <div key={name} style={{ margin: '10px 0' }}>
            <button onClick={() => setSelectedPlayer(selectedPlayer === name ? null : name)}>
              {selectedPlayer === name ? 'üîΩ' : '‚ñ∂Ô∏è'} {name}
            </button>
            {selectedPlayer === name && (
              <div>
                <table border="1" cellPadding="6">
                  <thead><tr><th>◊™◊ê◊®◊ô◊ö</th><th>RPE</th></tr></thead>
                  <tbody>
                    {playerData.slice(-8).map((d, i) => (
                      <tr key={i}><td>{d.date}</td><td>{d.avg}</td></tr>
                    ))}
                  </tbody>
                </table>
                <p>
                  3 ◊ô◊û◊ô◊ù: {rollingAverage(playerData, 3)} | 5 ◊ô◊û◊ô◊ù: {rollingAverage(playerData, 5)} | 8 ◊ô◊û◊ô◊ù: {rollingAverage(playerData, 8)}
                </p>
              </div>
            )}
          </div>
        );
      })}
    </div>
  );
}

export default function GymCourtReport() {
  return (
    <div className="report" dir="rtl" style={{ padding: '20px' }}>
      <h1>◊ì◊ï"◊ó RPE - ◊ó◊ì◊® ◊õ◊ï◊©◊® ◊ï◊û◊í◊®◊©</h1>
      <RPEReport title="üèãÔ∏è ◊ó◊ì◊® ◊õ◊ï◊©◊® (Gym)" tabName="Gym" fieldName="RPE ◊ë◊ó◊ì◊® ◊õ◊ï◊©◊®" />
      <RPEReport title="üèÄ ◊û◊í◊®◊© (Court)" tabName="Court" fieldName="RPE ◊ë◊û◊í◊®◊©" />
    </div>
  );
}
