import React, { useEffect, useState } from 'react';

function WellnessReport() {
  const [data, setData] = useState([]);
  const [averagesByDate, setAveragesByDate] = useState({});
  const [rollingAverages, setRollingAverages] = useState([]);
  const [customAverage, setCustomAverage] = useState(null);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');

  const sheetUrl = "https://opensheet.elk.sh/1x1e53gsx3VzEJ5ON5QOvYeJTGI8auMtSxJYYpUUmreY/Wallnes";

  useEffect(() => {
    fetch(sheetUrl)
      .then(res => res.json())
      .then(json => {
        const parsed = json.map(entry => ({
          name: entry["Name"],
          date: parseDate(entry["Timestamp"]),
          sleep: Number(entry["What is the quality of your sleep at night?"]),
          fatigue: Number(entry["How Fatigued are you?"]),
          sore: Number(entry["How sore are you?"])
        })).filter(e => !isNaN(e.date));

        setData(parsed);

        const groupedByDate = {};
        parsed.forEach(entry => {
          const d = entry.date.toISOString().split('T')[0];
          if (!groupedByDate[d]) groupedByDate[d] = [];
          groupedByDate[d].push(entry);
        });

        const dailyAverages = {};
        for (const date in groupedByDate) {
          const entries = groupedByDate[date];
          const sleepAvg = average(entries.map(e => e.sleep));
          const fatigueAvg = average(entries.map(e => e.fatigue));
          const soreAvg = average(entries.map(e => e.sore));
          dailyAverages[date] = {
            sleep: sleepAvg,
            fatigue: fatigueAvg,
            sore: soreAvg
          };
        }
        setAveragesByDate(dailyAverages);

        const sortedDates = Object.keys(dailyAverages).sort((a, b) => new Date(a) - new Date(b));
        const rolling = sortedDates.map((_, i) => {
          const recent = sortedDates.slice(Math.max(0, i - 7), i + 1);
          const values = recent.map(d => dailyAverages[d]);
          return {
            date: sortedDates[i],
            sleep: average(values.map(v => v.sleep)),
            fatigue: average(values.map(v => v.fatigue)),
            sore: average(values.map(v => v.sore))
          };
        });
        setRollingAverages(rolling);
      });
  }, []);

  function parseDate(dateString) {
    const [day, month, yearAndTime] = dateString.split('/');
    const [year, time] = yearAndTime.split(' ');
    return new Date(`${year}-${month}-${day}T${time}`);
  }

  function average(arr) {
    const valid = arr.filter(n => !isNaN(n));
    if (valid.length === 0) return ' 转';
    return (valid.reduce((a, b) => a + b, 0) / valid.length).toFixed(1);
  }

  function handleCustomRange() {
    if (!startDate || !endDate) return;
    const start = new Date(startDate);
    const end = new Date(endDate);
    const relevant = data.filter(entry => entry.date >= start && entry.date <= end);
    const sleep = average(relevant.map(e => e.sleep));
    const fatigue = average(relevant.map(e => e.fatigue));
    const sore = average(relevant.map(e => e.sore));
    setCustomAverage({ sleep, fatigue, sore });
  }

  const lastNDays = (n) => {
    const today = new Date();
    const dates = Object.keys(averagesByDate)
      .filter(d => {
        const date = new Date(d);
        return date <= today && date >= new Date(today.getTime() - (n * 24 * 60 * 60 * 1000));
      });
    const values = dates.map(d => averagesByDate[d]);
    return {
      sleep: average(values.map(v => v.sleep)),
      fatigue: average(values.map(v => v.fatigue)),
      sore: average(values.map(v => v.sore))
    };
  };

  return (
    <div className="wellness-report" dir="rtl" style={{ padding: '20px' }}>
      <h1> Wellness</h1>

      <h2>爪注 拽爪转 - 驻  (8  专)</h2>
      <table border="1" cellPadding="8" style={{ borderCollapse: "collapse", width: "100%", textAlign: "center" }}>
        <thead>
          <tr>
            <th>转专</th>
            <th>砖 </th>
            <th>注驻转 </th>
            <th> 砖专专 </th>
          </tr>
        </thead>
        <tbody>
          {Object.entries(averagesByDate)
            .sort((a, b) => new Date(b[0]) - new Date(a[0]))
            .slice(0, 8)
            .map(([date, avg]) => (
              <tr key={date}>
                <td>{date}</td>
                <td>{avg.sleep}</td>
                <td>{avg.fatigue}</td>
                <td>{avg.sore}</td>
              </tr>
            ))}
        </tbody>
      </table>

      <h2>爪注 拽爪转 转 (8  专)</h2>
      <table border="1" cellPadding="8" style={{ borderCollapse: "collapse", width: "100%", textAlign: "center" }}>
        <thead>
          <tr>
            <th>转专</th>
            <th>砖 </th>
            <th>注驻转 </th>
            <th> 砖专专 </th>
          </tr>
        </thead>
        <tbody>
          {rollingAverages.slice(-8).map((entry, i) => (
            <tr key={i}>
              <td>{entry.date}</td>
              <td>{entry.sleep}</td>
              <td>{entry.fatigue}</td>
              <td>{entry.sore}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h3>爪注 拽爪转: 3, 5 -8  专</h3>
      <ul>
        <li>3  专 - 砖: {lastNDays(3).sleep}, 注驻转: {lastNDays(3).fatigue}, 砖专专: {lastNDays(3).sore}</li>
        <li>5  专 - 砖: {lastNDays(5).sleep}, 注驻转: {lastNDays(5).fatigue}, 砖专专: {lastNDays(5).sore}</li>
        <li>8  专 - 砖: {lastNDays(8).sleep}, 注驻转: {lastNDays(8).fatigue}, 砖专专: {lastNDays(8).sore}</li>
      </ul>

      <h2>爪注 驻  转专</h2>
      <label>
        转专: <input type="date" value={startDate} onChange={e => setStartDate(e.target.value)} />
      </label>
      <label style={{ marginInlineStart: '10px' }}>
        注 转专: <input type="date" value={endDate} onChange={e => setEndDate(e.target.value)} />
      </label>
      <button onClick={handleCustomRange} style={{ marginInlineStart: '10px' }}>砖 爪注</button>

      {customAverage && (
        <div style={{ marginTop: '20px' }}>
          <h4>爪注  专:</h4>
          <p>砖: {customAverage.sleep}</p>
          <p>注驻转: {customAverage.fatigue}</p>
          <p> 砖专专: {customAverage.sore}</p>
        </div>
      )}
    </div>
  );
}

export default WellnessReport;
