// ExerciseLibrary.js
import React from 'react';
import './ExerciseLibrary.css';
import frontSquat from './images/front-squat.jpg';
import lunges from './images/lunges.jpg';
import legPress from './images/leg-press.jpg';
import legCurl from './images/leg-curl.jpg';
import pullUp from './images/pull-up.jpg';
import deadlift from './images/deadlift.jpg';
import row from './images/row.jpg';
import latPulldown from './images/lat-pulldown.jpg';
import pushUp from './images/push-up.jpg';
import benchPress from './images/bench-press.jpg';
import inclinePress from './images/incline-press.jpg';
import chestFly from './images/chest-fly.jpg';

const exerciseData = [
    {
      muscle: "Legs",
      exercises: [
        { name: "Front Squat", image: frontSquat },
        { name: "Lunges", image: lunges },
        { name: "Leg Press", image: legPress },
        { name: "Leg Curl", image: legCurl }
      ]
    },
    {
      muscle: "Back",
      exercises: [
        { name: "Pull-Up", image: pullUp },
        { name: "Deadlift", image: deadlift },
        { name: "Row", image: row },
        { name: "Lat Pulldown", image: latPulldown }
      ]
    },
    {
      muscle: "Chest",
      exercises: [
        { name: "Push-Up", image: pushUp },
        { name: "Bench Press", image: benchPress },
        { name: "Incline Press", image: inclinePress },
        { name: "Chest Fly", image: chestFly }
      ]
    }
  ];

export default function ExerciseLibrary() {
  return (
    <div style={{ padding: "20px" }} dir="rtl">
      <h1>Exercise Library</h1>
      <table className="exercise-table">
        <thead>
          <tr>
            {Array.from({ length: Math.max(...exerciseData.map(g => g.exercises.length)) }, (_, i) => (
              <th key={i}>#{i + 1}</th>
            )).reverse()}
            <th>Muscle Group</th>
          </tr>
        </thead>
        <tbody>
          {exerciseData.map((group, index) => {
            const exercisesReversed = group.exercises.slice().reverse();
            const emptyCells = Math.max(...exerciseData.map(g => g.exercises.length)) - exercisesReversed.length;

            return (
              <tr key={index}>
                {Array.from({ length: emptyCells }).map((_, idx) => (
                  <td key={`empty-${idx}`}></td>
                ))}
                {exercisesReversed.map((exercise, i) => (
                  <td key={i}>
                    <div><strong>{group.exercises.length - i}</strong></div>
                    <div>{exercise.name}</div>
                    {exercise.image ? (
                      <img
                        src={exercise.image}
                        alt={exercise.name}
                        className="exercise-image"
                      />
                    ) : (
                      <div className="no-image">No image</div>
                    )}
                  </td>
                ))}
                <td className="muscle-cell">{group.muscle}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}
