// ExerciseLibrary.js
import React, { useState } from 'react';

const exercises = [
  {
    name: 'Bodyweight Squat',
    category: 'Legs',
    tags: ['Rehab - Knee'],
    image: 'https://cdn.pixabay.com/photo/2017/03/02/02/41/squats-2117594_960_720.jpg'
  },
  {
    name: 'Lunges',
    category: 'Legs',
    tags: ['High Intensity'],
    image: 'https://cdn.pixabay.com/photo/2016/11/21/16/05/training-1840916_960_720.jpg'
  },
  {
    name: 'Push-ups',
    category: 'Chest',
    tags: [],
    image: 'https://cdn.pixabay.com/photo/2016/11/22/22/18/people-1850926_960_720.jpg'
  },
  {
    name: 'Plank',
    category: 'Core',
    tags: ['Rehab - Back'],
    image: 'https://cdn.pixabay.com/photo/2020/05/29/17/06/plank-5239461_960_720.jpg'
  },
  {
    name: 'Shoulder Press',
    category: 'Shoulders',
    tags: [],
    image: 'https://cdn.pixabay.com/photo/2017/06/18/19/19/fitness-2414866_960_720.jpg'
  },
  {
    name: 'Bicep Curls',
    category: 'Arms',
    tags: ['Rehab - Elbow'],
    image: 'https://cdn.pixabay.com/photo/2017/06/18/19/24/dumbbell-2414947_960_720.jpg'
  },
  {
    name: 'Deadlift',
    category: 'Back',
    tags: ['High Intensity'],
    image: 'https://cdn.pixabay.com/photo/2016/03/27/07/08/crossfit-1284616_960_720.jpg'
  },
  {
    name: 'Jumping Jacks',
    category: 'Cardio',
    tags: ['Low Intensity'],
    image: 'https://cdn.pixabay.com/photo/2017/03/02/02/41/jumping-2117589_960_720.jpg'
  }
];

const rehabOptions = [
  'All',
  'Rehab - Knee',
  'Rehab - Back',
  'Rehab - Elbow'
];

export default function ExerciseLibrary() {
  const [selectedRehab, setSelectedRehab] = useState('All');

  const categories = [...new Set(exercises.map(e => e.category))];

  const filteredExercises = selectedRehab === 'All'
    ? exercises
    : exercises.filter(e => e.tags.includes(selectedRehab));

  return (
    <div className="report" dir="rtl" style={{ padding: '20px' }}>
      <h1>Exercise Library</h1>

      <div style={{ marginBottom: '20px' }}>
        <label>Rehab Protocol:</label>
        <select value={selectedRehab} onChange={e => setSelectedRehab(e.target.value)}>
          {rehabOptions.map((option, idx) => (
            <option key={idx} value={option}>{option}</option>
          ))}
        </select>
      </div>

      {categories.map((category, idx) => (
        <div key={idx} style={{ marginBottom: '30px' }}>
          <h2>{category}</h2>
          <div style={{ display: 'flex', flexWrap: 'wrap', gap: '16px' }}>
            {filteredExercises.filter(e => e.category === category).map((ex, i) => (
              <div key={i} style={{ border: '1px solid #ccc', borderRadius: '8px', padding: '10px', width: '220px' }}>
                <img src={ex.image} alt={ex.name} style={{ width: '100%', height: '140px', objectFit: 'cover', borderRadius: '4px' }} />
                <h4 style={{ margin: '10px 0 0' }}>{ex.name}</h4>
                <p style={{ fontSize: '14px', color: '#666' }}>{ex.tags.join(', ')}</p>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}
