import React, { useState, useEffect } from 'react';
import dayjs from 'dayjs';
import 'dayjs/locale/he';

dayjs.locale('he');

const SHEET_BASE = "https://opensheet.elk.sh/1x1e53gsx3VzEJ5ON5QOvYeJTGI8auMtSxJYYpUUmreY";

const formatDate = (dateStr) => {
  const parsed = dayjs(dateStr, 'DD/MM/YYYY HH:mm:ss');
  return parsed.isValid() ? parsed.format('YYYY-MM-DD') : null;
};

function processRPEData(data, fieldName) {
  const daily = {};
  const playerDaily = {};

  data.forEach(entry => {
    const rawDate = entry.Timestamp;
    const name = entry.Name;
    const rpe = Number(entry[fieldName]);

    const date = formatDate(rawDate);
    if (!date || !name || isNaN(rpe)) return;

    if (!daily[date]) daily[date] = [];
    daily[date].push(rpe);

    if (!playerDaily[name]) playerDaily[name] = {};
    if (!playerDaily[name][date]) playerDaily[name][date] = [];
    playerDaily[name][date].push(rpe);
  });

  const dailyAvg = Object.entries(daily).map(([date, values]) => ({
    date,
    avg: (values.reduce((a, b) => a + b, 0) / values.length).toFixed(1)
  })).sort((a, b) => new Date(a.date) - new Date(b.date));

  return { dailyAvg, playerDaily };
}

function PlayerRPE({ playerData }) {
  const [selectedPlayer, setSelectedPlayer] = useState(null);
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [customAvg, setCustomAvg] = useState(null);

  const getPlayerRollingAvg = (data, days) => {
    const dates = Object.keys(data).sort((a, b) => new Date(a) - new Date(b));
    const lastDates = dates.slice(-days);
    const values = lastDates.map(d => {
      const arr = data[d];
      return arr.length ? arr.reduce((a, b) => a + b, 0) / arr.length : null;
    }).filter(Boolean);
    return values.length ? (values.reduce((a, b) => a + b, 0) / values.length).toFixed(1) : '◊ê◊ô◊ü ◊†◊™◊ï◊†◊ô◊ù';
  };

  const getCustomAvg = (data, from, to) => {
    const filtered = Object.entries(data).filter(([d]) => d >= from && d <= to);
    const values = filtered.map(([, arr]) => arr.reduce((a, b) => a + b, 0) / arr.length);
    return values.length ? (values.reduce((a, b) => a + b, 0) / values.length).toFixed(1) : '◊ê◊ô◊ü ◊†◊™◊ï◊†◊ô◊ù';
  };

  return (
    <>
      {Object.entries(playerData).map(([name, data], idx) => (
        <div key={idx} style={{ margin: '16px 0' }}>
          <button onClick={() => setSelectedPlayer(selectedPlayer === name ? null : name)}>
            {selectedPlayer === name ? 'üîΩ' : '‚ñ∂Ô∏è'} {name}
          </button>
          {selectedPlayer === name && (
            <div style={{ marginTop: 10 }}>
              <table border="1" cellPadding="6">
                <thead><tr><th>◊™◊ê◊®◊ô◊ö</th><th>RPE</th></tr></thead>
                <tbody>
                  {Object.entries(data).sort((a, b) => new Date(a[0]) - new Date(b[0])).slice(-8).map(([date, values], i) => (
                    <tr key={i}>
                      <td>{date}</td>
                      <td>{(values.reduce((a, b) => a + b, 0) / values.length).toFixed(1)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
              <p>◊û◊û◊ï◊¶◊¢◊ô◊ù ◊û◊™◊í◊ú◊í◊ú◊ô◊ù: 3 ◊ô◊û◊ô◊ù: {getPlayerRollingAvg(data, 3)} | 5 ◊ô◊û◊ô◊ù: {getPlayerRollingAvg(data, 5)} | 8 ◊ô◊û◊ô◊ù: {getPlayerRollingAvg(data, 8)}</p>

              <div>
                <p>◊û◊û◊ï◊¶◊¢ ◊ú◊§◊ô ◊ò◊ï◊ï◊ó ◊™◊ê◊®◊ô◊õ◊ô◊ù</p>
                <input type="date" value={from} onChange={e => setFrom(e.target.value)} />
                <input type="date" value={to} onChange={e => setTo(e.target.value)} />
                <button onClick={() => setCustomAvg(getCustomAvg(data, from, to))}>◊ó◊©◊ë ◊û◊û◊ï◊¶◊¢</button>
                {customAvg && <p>◊û◊û◊ï◊¶◊¢ ◊ë◊ò◊ï◊ï◊ó: {customAvg}</p>}
              </div>
            </div>
          )}
        </div>
      ))}
    </>
  );
}

function RPEBlock({ title, url, field }) {
  const [data, setData] = useState([]);
  const [processed, setProcessed] = useState(null);
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [customAvg, setCustomAvg] = useState(null);

  useEffect(() => {
    fetch(url)
      .then(res => res.json())
      .then(json => {
        setData(json);
        setProcessed(processRPEData(json, field));
      });
  }, [url, field]);

  const getRollingAvg = (days) => {
    if (!processed) return 0;
    const recent = processed.dailyAvg.slice(-days);
    const values = recent.map(d => Number(d.avg));
    return values.length ? (values.reduce((a, b) => a + b, 0) / values.length).toFixed(1) : '◊ê◊ô◊ü ◊†◊™◊ï◊†◊ô◊ù';
  };

  const getCustomAvg = (from, to) => {
    if (!processed) return 0;
    const filtered = processed.dailyAvg.filter(d => d.date >= from && d.date <= to);
    const values = filtered.map(d => Number(d.avg));
    return values.length ? (values.reduce((a, b) => a + b, 0) / values.length).toFixed(1) : '◊ê◊ô◊ü ◊†◊™◊ï◊†◊ô◊ù';
  };

  return (
    <div style={{ marginBottom: '40px' }}>
