// ExerciseLibrary.js
import React from 'react';
import frontSquatImg from './images/front-squat.jpg';

const exercises = [
  {
    name: 'Front Squat',
    category: 'Legs',
    tags: ['Medium Intensity'],
    image: frontSquatImg
  }
];

const groupedExercises = exercises.reduce((acc, exercise) => {
  if (!acc[exercise.category]) acc[exercise.category] = [];
  acc[exercise.category].push(exercise);
  return acc;
}, {});

export default function ExerciseLibrary() {
  return (
    <div dir="rtl" style={{ padding: '20px' }}>
      <h1>Exercise Library</h1>
      <table border="1" cellPadding="10" style={{ width: '100%', textAlign: 'center', direction: 'rtl' }}>
        <thead>
          <tr>
            <th style={{ backgroundColor: '#f0f0f0' }}>Muscle Group</th>
            <th>Exercises</th>
          </tr>
        </thead>
        <tbody>
          {Object.entries(groupedExercises).map(([category, exs], idx) => (
            <tr key={idx}>
              <td style={{ fontWeight: 'bold', backgroundColor: '#f9f9f9' }}>{category}</td>
              <td>
                {exs.map((ex, i) => (
                  <div key={i} style={{ marginBottom: '20px' }}>
                    <h3>{ex.name}</h3>
                    <img
                      src={ex.image}
                      alt={ex.name}
                      style={{ width: '200px', height: 'auto', objectFit: 'contain' }}
                    />
                  </div>
                ))}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
