import React, { useEffect, useState } from 'react';

function WellnessReport() {
  const [data, setData] = useState([]);
  const [rollingAverages, setRollingAverages] = useState({});
  const [playerAverages, setPlayerAverages] = useState({});
  const [selectedMetric, setSelectedMetric] = useState('sleep');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [filteredAverage, setFilteredAverage] = useState(null);

  const sheetUrl = "https://opensheet.elk.sh/1x1e53gsx3VzEJ5ON5QOvYeJTGI8auMtSxJYYpUUmreY/Wallnes";

  useEffect(() => {
    fetch(sheetUrl)
      .then(res => res.json())
      .then(json => {
        const parsedData = json.map(entry => ({
          name: entry["Name"],
          sleep: Number(entry["What is the quality of your sleep at night?"]),
          sore: Number(entry["How sore are you?"]),
          fatigue: Number(entry["How Fatigued are you?"]),
          date: new Date(entry.Timestamp)
        })).filter(entry => !isNaN(entry.date));

        setData(parsedData);
        calculateRollingAverages(parsedData);
        calculatePlayerAverages(parsedData);
      });
  }, []);

  const calculateRollingAverages = (entries) => {
    const groupedByDate = {};
    entries.forEach(entry => {
      const dateKey = entry.date.toISOString().split('T')[0];
      if (!groupedByDate[dateKey]) groupedByDate[dateKey] = [];
      groupedByDate[dateKey].push(entry);
    });

    const result = {};
    Object.keys(groupedByDate).slice(-5).forEach(date => {
      const group = groupedByDate[date];
      const total = { sleep: 0, sore: 0, fatigue: 0 };
      group.forEach(e => {
        total.sleep += e.sleep;
        total.sore += e.sore;
        total.fatigue += e.fatigue;
      });
      result[date] = {
        sleep: (total.sleep / group.length).toFixed(2),
        sore: (total.sore / group.length).toFixed(2),
        fatigue: (total.fatigue / group.length).toFixed(2)
      };
    });
    setRollingAverages(result);
  };

  const calculatePlayerAverages = (entries) => {
    const players = {};
    entries.forEach(entry => {
      if (!players[entry.name]) players[entry.name] = [];
      players[entry.name].push(entry);
    });
    setPlayerAverages(players);
  };

  const handleDateRangeAverage = () => {
    const start = new Date(startDate);
    const end = new Date(endDate);
    if (isNaN(start) || isNaN(end)) return;

    const filtered = data.filter(entry => entry.date >= start && entry.date <= end);
    const total = filtered.reduce((sum, e) => sum + e[selectedMetric], 0);
    const avg = (total / filtered.length).toFixed(2);
    setFilteredAverage(avg);
  };

  return (
    <div className="wellness-report" dir="rtl" style={{ padding: '20px' }}>
      <h1> Wellness</h1>

      <h2> 爪注 拽爪转 转 (5  专)</h2>
      <table border="1" cellPadding="8" style={{ width: '100%', textAlign: 'center' }}>
        <thead>
          <tr>
            <th>转专</th>
            <th>砖 </th>
            <th>注驻转 </th>
            <th> </th>
          </tr>
        </thead>
        <tbody>
          {Object.keys(rollingAverages).map(date => (
            <tr key={date}>
              <td>{date}</td>
              <td>{rollingAverages[date].sleep}</td>
              <td>{rollingAverages[date].fatigue}</td>
              <td>{rollingAverages[date].sore}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2> 拽转 爪注 驻  转专</h2>
      <div style={{ marginBottom: '10px' }}>
        <label>砖转: </label>
        <select value={selectedMetric} onChange={(e) => setSelectedMetric(e.target.value)}>
          <option value="sleep">砖</option>
          <option value="fatigue">注驻转</option>
          <option value="sore"> 砖专专</option>
        </select>
        <br /><br />
        <label>转专: </label>
        <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} />
        <label> 注: </label>
        <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} />
        <button onClick={handleDateRangeAverage}>砖 爪注</button>
      </div>

      {filteredAverage !== null && (
        <p><strong>爪注 {selectedMetric}  转专:</strong> {filteredAverage}</p>
      )}

      <h2> 爪注 驻 砖拽转</h2>
      {Object.keys(playerAverages).map(name => {
        const entries = playerAverages[name];
        const recent = entries.slice(-5);
        const avg = key => (recent.reduce((sum, e) => sum + e[key], 0) / recent.length).toFixed(2);

        return (
          <div key={name} style={{ marginBottom: '20px' }}>
            <details>
              <summary><strong>{name}</strong></summary>
              <p>爪注 砖 (5 ): {avg('sleep')}</p>
              <p>爪注 注驻转 (5 ): {avg('fatigue')}</p>
              <p>爪注  (5 ): {avg('sore')}</p>
            </details>
          </div>
        );
      })}
    </div>
  );
}

export default WellnessReport;
