import React, { useEffect, useState } from 'react';

function WellnessReport() {
  const [data, setData] = useState([]);
  const [dailyAverages, setDailyAverages] = useState({});
  const [rollingTeamAverages, setRollingTeamAverages] = useState({});
  const [playerRollingAverages, setPlayerRollingAverages] = useState({});
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [filteredVariable, setFilteredVariable] = useState('sleep');
  const [filteredAverage, setFilteredAverage] = useState(null);

  const sheetUrl = "https://opensheet.elk.sh/1x1e53gsx3VzEJ5ON5QOvYeJTGI8auMtSxJYYpUUmreY/Wallnes";

  useEffect(() => {
    fetch(sheetUrl)
      .then(res => res.json())
      .then(json => {
        setData(json);
        calculateDailyAverages(json);
        calculateRollingAverages(json);
      });
  }, []);

  const calculateDailyAverages = (json) => {
    const result = {};
    json.forEach(entry => {
      const date = entry.Date;
      const sleep = Number(entry["What is the quality of your sleep at night?"]);
      const sore = Number(entry["How sore are you?"]);
      const fatigue = Number(entry["How Fatigued are you?"]);

      if (!result[date]) {
        result[date] = { count: 0, sleep: 0, sore: 0, fatigue: 0 };
      }

      result[date].count++;
      result[date].sleep += sleep;
      result[date].sore += sore;
      result[date].fatigue += fatigue;
    });

    const formatted = {};
    for (const date in result) {
      const item = result[date];
      formatted[date] = {
        sleep: (item.sleep / item.count).toFixed(2),
        sore: (item.sore / item.count).toFixed(2),
        fatigue: (item.fatigue / item.count).toFixed(2),
      };
    }

    setDailyAverages(formatted);
  };

  const calculateRollingAverages = (json) => {
    const sorted = [...json].sort((a, b) => new Date(a.Date) - new Date(b.Date));

    const playerData = {};
    const teamData = [];

    sorted.forEach(entry => {
      const name = entry.Name;
      const date = entry.Date;
      const sleep = Number(entry["What is the quality of your sleep at night?"]);
      const sore = Number(entry["How sore are you?"]);
      const fatigue = Number(entry["How Fatigued are you?"]);

      if (!playerData[name]) playerData[name] = [];
      playerData[name].push({ date, sleep, sore, fatigue });
      teamData.push({ date, sleep, sore, fatigue });
    });

    const teamRolling = {};
    const dates = [...new Set(teamData.map(e => e.date))].slice(-5);

    dates.forEach((_, i, arr) => {
      const window = arr.slice(0, i + 1);
      const filtered = teamData.filter(e => window.includes(e.date));
      const count = filtered.length;
      const avg = {
        sleep: (filtered.reduce((a, b) => a + b.sleep, 0) / count).toFixed(2),
        sore: (filtered.reduce((a, b) => a + b.sore, 0) / count).toFixed(2),
        fatigue: (filtered.reduce((a, b) => a + b.fatigue, 0) / count).toFixed(2),
      };
      teamRolling[window[window.length - 1]] = avg;
    });

    setRollingTeamAverages(teamRolling);

    const playerRolling = {};
    Object.entries(playerData).forEach(([name, entries]) => {
      const recent = entries.slice(-5);
      const count = recent.length;
      const avg = {
        sleep: (recent.reduce((a, b) => a + b.sleep, 0) / count).toFixed(2),
        sore: (recent.reduce((a, b) => a + b.sore, 0) / count).toFixed(2),
        fatigue: (recent.reduce((a, b) => a + b.fatigue, 0) / count).toFixed(2),
      };
      playerRolling[name] = avg;
    });
    setPlayerRollingAverages(playerRolling);
  };

  const calculateFilteredAverage = () => {
    const start = new Date(startDate);
    const end = new Date(endDate);

    if (isNaN(start.getTime()) || isNaN(end.getTime())) {
      alert("砖  转专 转拽.");
      return;
    }

    const filtered = data.filter(entry => {
      const entryDate = new Date(entry.Date);
      return entryDate >= start && entryDate <= end;
    });

    const count = filtered.length;
    const sum = filtered.reduce((acc, curr) => acc + Number(curr[filteredVariable]), 0);
    const avg = (sum / count).toFixed(2);
    setFilteredAverage(avg);
  };

  return (
    <div className="wellness-report" dir="rtl" style={{ padding: '20px' }}>
      <h1> Wellness</h1>

      <h2>爪注 拽爪转 转 (5  专)</h2>
      <table border="1" cellPadding="8" style={{ borderCollapse: "collapse", width: "100%", textAlign: "center" }}>
        <thead>
          <tr>
            <th>转专</th>
            <th>砖 </th>
            <th>注驻转 </th>
            <th> 砖专专 </th>
          </tr>
        </thead>
        <tbody>
          {Object.entries(rollingTeamAverages).map(([date, avg]) => (
            <tr key={date}>
              <td>{date}</td>
              <td>{avg.sleep}</td>
              <td>{avg.fatigue}</td>
              <td>{avg.sore}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h3>砖 爪注 驻  转专</h3>
      <div>
        <label>转专: <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} /></label>
        <label>注 转专: <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} /></label>
        <label>
          砖转:
          <select value={filteredVariable} onChange={(e) => setFilteredVariable(e.target.value)}>
            <option value="What is the quality of your sleep at night?">砖</option>
            <option value="How Fatigued are you?">注驻转</option>
            <option value="How sore are you?"> 砖专专</option>
          </select>
        </label>
        <button onClick={calculateFilteredAverage}>砖 爪注</button>
        {filteredAverage && <p>爪注  砖专: {filteredAverage}</p>}
      </div>
    </div>
  );
}

export default WellnessReport;
