import React, { useEffect, useState } from 'react';

function WellnessReport() {
  const [data, setData] = useState([]);
  const [dailyAverages, setDailyAverages] = useState([]);
  const [rollingAverages, setRollingAverages] = useState([]);
  const [dateRange, setDateRange] = useState({ from: '', to: '' });
  const [playerStats, setPlayerStats] = useState({});
  const [expandedPlayers, setExpandedPlayers] = useState([]);
  const sheetUrl = "https://opensheet.elk.sh/1x1e53gsx3VzEJ5ON5QOvYeJTGI8auMtSxJYYpUUmreY/Wallnes";

  useEffect(() => {
    fetch(sheetUrl)
      .then(res => res.json())
      .then(json => {
        const parsed = json.map(entry => ({
          name: entry["Name"],
          date: new Date(entry["Date"]),
          sleep: Number(entry["What is the quality of your sleep at night?"]),
          sore: Number(entry["How sore are you?"]),
          fatigue: Number(entry["How Fatigued are you?"])
        })).filter(entry => !isNaN(entry.date));

        setData(parsed);

        // Daily Averages
        const dailyMap = {};
        parsed.forEach(entry => {
          const key = entry.date.toISOString().split('T')[0];
          if (!dailyMap[key]) dailyMap[key] = [];
          dailyMap[key].push(entry);
        });

        const dailyAvg = Object.entries(dailyMap).map(([date, entries]) => {
          const total = entries.reduce((acc, e) => ({
            sleep: acc.sleep + e.sleep,
            sore: acc.sore + e.sore,
            fatigue: acc.fatigue + e.fatigue
          }), { sleep: 0, sore: 0, fatigue: 0 });

          const count = entries.length;
          return {
            date,
            sleep: (total.sleep / count).toFixed(2),
            sore: (total.sore / count).toFixed(2),
            fatigue: (total.fatigue / count).toFixed(2)
          };
        }).sort((a, b) => new Date(b.date) - new Date(a.date));

        setDailyAverages(dailyAvg);

        // Player Stats
        const playersMap = {};
        parsed.forEach(entry => {
          if (!playersMap[entry.name]) playersMap[entry.name] = [];
          playersMap[entry.name].push(entry);
        });

        setPlayerStats(playersMap);
        calculateRollingAverages(parsed);
      });
  }, []);

  const calculateRollingAverages = (entries) => {
    const sorted = [...entries].sort((a, b) => new Date(b.date) - new Date(a.date));
    const recent = sorted.slice(0, 5);
    const total = recent.reduce((acc, e) => ({
      sleep: acc.sleep + e.sleep,
      sore: acc.sore + e.sore,
      fatigue: acc.fatigue + e.fatigue
    }), { sleep: 0, sore: 0, fatigue: 0 });

    const count = recent.length;
    setRollingAverages([{ days: 5, sleep: (total.sleep / count).toFixed(2), sore: (total.sore / count).toFixed(2), fatigue: (total.fatigue / count).toFixed(2) }]);
  };

  const handleRangeAverage = () => {
    if (!dateRange.from || !dateRange.to) return;
    const from = new Date(dateRange.from);
    const to = new Date(dateRange.to);
    const filtered = data.filter(e => e.date >= from && e.date <= to);
    if (!filtered.length) return;

    const total = filtered.reduce((acc, e) => ({
      sleep: acc.sleep + e.sleep,
      sore: acc.sore + e.sore,
      fatigue: acc.fatigue + e.fatigue
    }), { sleep: 0, sore: 0, fatigue: 0 });
    const count = filtered.length;

    setRollingAverages([{
      days: `${dateRange.from} - ${dateRange.to}`,
      sleep: (total.sleep / count).toFixed(2),
      sore: (total.sore / count).toFixed(2),
      fatigue: (total.fatigue / count).toFixed(2)
    }]);
  };

  const togglePlayer = (name) => {
    setExpandedPlayers(prev => prev.includes(name) ? prev.filter(p => p !== name) : [...prev, name]);
  };

  return (
    <div dir="rtl" style={{ padding: '20px' }}>
      <h1> Wellness</h1>

      <h2>爪注 拽爪转 - 砖  专</h2>
      <table>
        <thead>
          <tr><th>转专</th><th>砖 </th><th>注驻转 </th><th> 砖专专 </th></tr>
        </thead>
        <tbody>
          {dailyAverages.slice(0, 5).map((avg, i) => (
            <tr key={i}>
              <td>{avg.date}</td>
              <td>{avg.sleep}</td>
              <td>{avg.fatigue}</td>
              <td>{avg.sore}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h3>爪注 拽爪转 驻  转专</h3>
      <input type="date" value={dateRange.from} onChange={e => setDateRange({ ...dateRange, from: e.target.value })} />
      <input type="date" value={dateRange.to} onChange={e => setDateRange({ ...dateRange, to: e.target.value })} />
      <button onClick={handleRangeAverage}>砖 爪注</button>

      {rollingAverages.length > 0 && (
        <table>
          <thead><tr><th></th><th>砖 </th><th>注驻转 </th><th> 砖专专 </th></tr></thead>
          <tbody>
            {rollingAverages.map((avg, i) => (
              <tr key={i}>
                <td>{avg.days}</td>
                <td>{avg.sleep}</td>
                <td>{avg.fatigue}</td>
                <td>{avg.sore}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <h2>爪注 驻 砖拽转</h2>
      {Object.keys(playerStats).map(name => (
        <div key={name} style={{ marginBottom: '10px' }}>
          <button onClick={() => togglePlayer(name)} style={{ fontWeight: 'bold', fontSize: '16px' }}>{name}</button>
          {expandedPlayers.includes(name) && (
            <div style={{ marginTop: '5px' }}>
              <table>
                <thead><tr><th>转专</th><th>砖</th><th>注驻转</th><th></th></tr></thead>
                <tbody>
                  {playerStats[name].sort((a, b) => new Date(b.date) - new Date(a.date)).slice(0, 5).map((e, i) => (
                    <tr key={i}>
                      <td>{e.date.toISOString().split('T')[0]}</td>
                      <td>{e.sleep}</td>
                      <td>{e.fatigue}</td>
                      <td>{e.sore}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
              <p>猬锔 拽专: 爪注 砖 驻  转专 ( 专拽  拽爪转)</p>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default WellnessReport;
