import React, { useState, useEffect } from 'react';
import './App.css';

const defaultPlayers = [
  { name: "×©×™×¨×”", active: true },
  { name: "× ×•×¢×”", active: true },
  { name: "×“× ×”", active: true },
  { name: "×¨×•×ª", active: true },
  { name: "×”×™×œ×”", active: true }
];

const getSavedPlayers = () => {
  const saved = localStorage.getItem("players");
  return saved ? JSON.parse(saved) : defaultPlayers;
};

function formatTime(sec) {
  const minutes = String(Math.floor(sec / 60)).padStart(2, '0');
  const seconds = String(sec % 60).padStart(2, '0');
  return `${minutes}:${seconds}`;
}

function MainGame() {
  const [quarter, setQuarter] = useState(1);
  const [timeLeft, setTimeLeft] = useState(600);
  const [totalGameTime, setTotalGameTime] = useState(0);
  const [running, setRunning] = useState(false);
  const [showHistory, setShowHistory] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [editOpponentsMode, setEditOpponentsMode] = useState(false);
  const [timeoutMode, setTimeoutMode] = useState(false);
  const [breakMode, setBreakMode] = useState(false);
  const [editClockMode, setEditClockMode] = useState(false);
  const [editClockValue, setEditClockValue] = useState(600);
  const [timeoutStart, setTimeoutStart] = useState(null);

  const [players, setPlayers] = useState(getSavedPlayers().map(p => ({
    name: p.name,
    active: p.active,
    total: 0,
    startTime: null,
    restStart: 0,
    fouls: 0,
    points: 0,
    history: [],
    timeoutRest: false
  })));

  const [opponents, setOpponents] = useState([]);

  useEffect(() => {
    let timer;
    if (running && timeLeft > 0 && !timeoutMode && !breakMode) {
      timer = setInterval(() => {
        setTimeLeft(prev => prev - 1);
        setTotalGameTime(prev => prev + 1);
      }, 1000);
    }
    if (timeLeft === 0 && running) {
      setRunning(false);
      setBreakMode(true);
      alert(`×¡×™×•× ×¨×‘×¢ ${quarter}`);
      if (quarter < 4) {
        setQuarter(prev => prev + 1);
        setTimeLeft(600);
      }
    }
    return () => clearInterval(timer);
  }, [running, timeLeft, quarter, timeoutMode, breakMode]);

  const startQuarter = () => {
    setRunning(true);
    setTimeoutMode(false);
    setBreakMode(false);
  };

  const stopQuarter = () => setRunning(false);

  const handleTimeout = () => {
    setRunning(false);
    setTimeoutMode(true);
    setTimeoutStart(totalGameTime);
  };

  const resumeFromTimeout = () => {
    const timeoutEnd = totalGameTime;
    setPlayers(prev =>
      prev.map(p => {
        if (p.startTime === null) {
          return {
            ...p,
            restStart: timeoutStart !== null ? timeoutStart : p.restStart,
            timeoutRest: true
          };
        }
        return p;
      })
    );
    setTimeoutMode(false);
    setTimeoutStart(null);
    setRunning(true);
  };

  const togglePlayer = (index) => {
    setPlayers(prev => {
      const updated = [...prev];
      const p = updated[index];
      if (p.startTime === null) {
        updated[index] = {
          ...p,
          startTime: totalGameTime,
          restStart: 0,
          timeoutRest: false,
          history: [...p.history, { action: 'in', time: totalGameTime }]
        };
      } else {
        const newTotal = p.total + (totalGameTime - p.startTime);
        updated[index] = {
          ...p,
          startTime: null,
          total: newTotal,
          restStart: totalGameTime,
          history: [...p.history, { action: 'out', time: totalGameTime }]
        };
      }
      return updated;
    });
  };
  const changeValue = (listSetter, index, key, delta) => {
    listSetter(prev => {
      const updated = [...prev];
      updated[index][key] = Math.max(0, updated[index][key] + delta);
      return updated;
    });
  };

  const savePlayers = () => {
    localStorage.setItem("players", JSON.stringify(players.map(p => ({
      name: p.name,
      active: p.active
    }))));
    setEditMode(false);
  };

  const saveOpponents = () => setEditOpponentsMode(false);

  return (
    <div className="App" dir="rtl">
      <h1>×‘×“×™×§×ª ×“×§×•×ª ××©×—×§</h1>

      <div className="controls">
        <div className="section">
          <h3>×©×œ×™×˜×” ×‘××©×—×§</h3>
          <button onClick={startQuarter}>×”×ª×—×œ</button>
          <button onClick={stopQuarter}>×¢×¦×•×¨</button>
          <button onClick={() => { setTimeoutMode(true); stopQuarter(); setTimeoutStart(totalGameTime); }}>â± ×˜×™×™× ×××•×˜</button>
          <button onClick={() => { setBreakMode(true); stopQuarter(); }}>ğŸ“£ ×”×¤×¡×§×ª ×¨×‘×¢</button>
          <button onClick={() => setEditClockMode(!editClockMode)}>â° ×¡× ×›×¨×•×Ÿ ×©×¢×•×Ÿ</button>
        </div>
        <div className="section">
          <h3>× ×™×•×•×˜</h3>
          <button onClick={() => { setShowHistory(false); setEditMode(false); setEditOpponentsMode(false); }}>×—×–×¨×” ×œ××©×—×§</button>
          <button onClick={() => { setShowHistory(true); setEditMode(false); setEditOpponentsMode(false); }}>×”×™×¡×˜×•×¨×™×™×ª ×›× ×™×¡×•×ª</button>
        </div>
        <div className="section">
          <h3>× ×™×”×•×œ ×§×‘×•×¦×•×ª</h3>
          <button onClick={() => { setEditMode(!editMode); setShowHistory(false); setEditOpponentsMode(false); }}>× ×™×”×•×œ ×§×‘×•×¦×”</button>
          <button onClick={() => { setEditOpponentsMode(!editOpponentsMode); setShowHistory(false); setEditMode(false); }}>× ×™×”×•×œ ×§×‘×•×¦×” ×™×¨×™×‘×”</button>
        </div>
      </div>

      {editClockMode && (
        <div className="sync-clock">
          <input
            type="number"
            value={editClockValue}
            onChange={(e) => setEditClockValue(Number(e.target.value))}
          />
          <button onClick={() => setTimeLeft(editClockValue)}>×¢×“×›×Ÿ ×©×¢×•×Ÿ</button>
        </div>
      )}

      {!showHistory && !editMode && !editOpponentsMode && (
        <>
          <h2>×¨×‘×¢: {quarter} | ×©×¢×•×Ÿ: {formatTime(timeLeft)}</h2>
          {timeoutMode && <p className="orange">â± ×˜×™×™× ×××•×˜</p>}
          {breakMode && <p className="green">ğŸ“£ ×”×¤×¡×§×ª ×¨×‘×¢</p>}

          <h3>×©×—×§× ×™×•×ª</h3>
          <table>
            <thead>
              <tr>
                <th>×©×</th>
                <th>×¨×¦×•×£</th>
                <th>×× ×•×—×”</th>
                <th>×¡×”"×›</th>
                <th>×¤×¢×•×œ×”</th>
                <th>× ×§'</th>
                <th>+/-</th>
                <th>×¢×‘'</th>
                <th>+/-</th>
              </tr>
            </thead>
            <tbody>
              {players.filter(p => p.active).map((p, i) => {
                const onCourt = p.startTime !== null;
                const restTime =
                  !onCourt && p.restStart !== 0
                    ? totalGameTime - p.restStart
                    : 0;
                const playingTime =
                  p.total + (onCourt ? totalGameTime - p.startTime : 0);

                return (
                  <tr key={i}>
                    <td>
                      {p.name}
                      {p.starForTimeout ? ' *' : ''}
                    </td>
                    <td>{onCourt ? formatTime(totalGameTime - p.startTime) : '-'}</td>
                    <td>{restTime > 0 ? formatTime(restTime) : '-'}</td>
                    <td>{formatTime(playingTime)}</td>
                    <td>
                      <button onClick={() => togglePlayer(i)}>
                        {onCourt ? '×”×•×¦×' : '×”×›× ×¡'}
                      </button>
                    </td>
                    <td>{p.points}</td>
                    <td>
                      <button onClick={() => changeValue(setPlayers, i, 'points', 1)}>+1</button>
                      <button onClick={() => changeValue(setPlayers, i, 'points', -1)}>-1</button>
                    </td>
                    <td>{p.fouls}</td>
                    <td>
                      <button onClick={() => changeValue(setPlayers, i, 'fouls', 1)}>+1</button>
                      <button onClick={() => changeValue(setPlayers, i, 'fouls', -1)}>-1</button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>

          <h3>×§×‘×•×¦×ª ×™×¨×™×‘×”</h3>
          <table>
            <thead>
              <tr>
                <th>×©×</th>
                <th>×¨×¦×•×£</th>
                <th>×× ×•×—×”</th>
                <th>×¡×”"×›</th>
                <th>×¤×¢×•×œ×”</th>
                <th>× ×§'</th>
                <th>+/-</th>
                <th>×¢×‘'</th>
                <th>+/-</th>
              </tr>
            </thead>
            <tbody>
              {opponents.filter(p => p.active !== false).map((p, i) => {
                const onCourt = p.startTime !== null;
                const restTime =
                  !onCourt && p.restStart !== 0
                    ? totalGameTime - p.restStart
                    : 0;
                const playingTime =
                  p.total + (onCourt ? totalGameTime - p.startTime : 0);

                return (
                  <tr key={i}>
                    <td>{p.name}</td>
                    <td>{onCourt ? formatTime(totalGameTime - p.startTime) : '-'}</td>
                    <td>{restTime > 0 ? formatTime(restTime) : '-'}</td>
                    <td>{formatTime(playingTime)}</td>
                    <td>
                      <button onClick={() => toggleOpponent(i)}>
                        {onCourt ? '×”×•×¦×' : '×”×›× ×¡'}
                      </button>
                    </td>
                    <td>{p.points}</td>
                    <td>
                      <button onClick={() => changeValue(setOpponents, i, 'points', 1)}>+1</button>
                      <button onClick={() => changeValue(setOpponents, i, 'points', 2)}>+2</button>
                      <button onClick={() => changeValue(setOpponents, i, 'points', 3)}>+3</button>
                      <button onClick={() => changeValue(setOpponents, i, 'points', -1)}>-1</button>
                    </td>
                    <td>{p.fouls}</td>
                    <td>
                      <button onClick={() => changeValue(setOpponents, i, 'fouls', 1)}>+1</button>
                      <button onClick={() => changeValue(setOpponents, i, 'fouls', -1)}>-1</button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </>
      )}

      {showHistory && (
        <>
          <h2>×”×™×¡×˜×•×¨×™×™×ª ×›× ×™×¡×•×ª ×•×™×¦×™××•×ª</h2>
          {players.map((p, i) => (
            <div key={i} style={{ marginBottom: '16px' }}>
              <h4>{p.name}</h4>
              <table>
                <thead>
                  <tr>
                    <th>×¤×¢×•×œ×”</th>
                    <th>×©× ×™×•×ª</th>
                    <th>×©×¢×”</th>
                  </tr>
                </thead>
                <tbody>
                  {p.history.map((h, j) => (
                    <tr key={j}>
                      <td>{h.action === 'in' ? '×›× ×™×¡×”' : '×™×¦×™××”'}</td>
                      <td>{h.time}</td>
                      <td>{formatTime(h.time)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))}
        </>
      )}

      {editMode && (
        <div>
          <h3>× ×™×”×•×œ ×§×‘×•×¦×”</h3>
          {players.map((p, i) => (
            <div key={i}>
              <input
                type="text"
                value={p.name}
                onChange={(e) => {
                  const updated = [...players];
