import React, { useEffect, useState } from 'react';

function WellnessReport() {
  const [data, setData] = useState([]);
  const [dateRange, setDateRange] = useState({ from: '', to: '' });
  const [rangeAverage, setRangeAverage] = useState(null);
  const [dailyGroupAverages, setDailyGroupAverages] = useState([]);
  const [rollingAverages, setRollingAverages] = useState({});
  const [playerNames, setPlayerNames] = useState([]);
  const [expandedPlayers, setExpandedPlayers] = useState({});

  const sheetUrl = "https://opensheet.elk.sh/1x1e53gsx3VzEJ5ON5QOvYeJTGI8auMtSxJYYpUUmreY/Wallnes";

  useEffect(() => {
    fetch(sheetUrl)
      .then(res => res.json())
      .then(json => {
        const parsed = json.map(entry => ({
          name: entry["Name"],
          date: parseDate(entry["Timestamp"]),
          sleep: Number(entry["What is the quality of your sleep at night?"] || 0),
          sore: Number(entry["How sore are you?"] || 0),
          fatigue: Number(entry["How Fatigued are you?"] || 0),
        })).filter(entry => entry.name && !isNaN(entry.date));

        setData(parsed);
        setPlayerNames([...new Set(parsed.map(e => e.name))]);

        const recentDays = getLastNDates(8);
        const groupAveragesByDay = recentDays.map(date => {
          const daily = parsed.filter(e => formatDate(e.date) === formatDate(date));
          return {
            date: formatDate(date),
            sleep: average(daily.map(e => e.sleep)),
            sore: average(daily.map(e => e.sore)),
            fatigue: average(daily.map(e => e.fatigue))
          };
        });
        setDailyGroupAverages(groupAveragesByDay);

        const groupedByPlayer = {};
        parsed.forEach(e => {
          if (!groupedByPlayer[e.name]) groupedByPlayer[e.name] = [];
          groupedByPlayer[e.name].push(e);
        });

        const today = new Date();
        const newRolling = {};
        Object.entries(groupedByPlayer).forEach(([name, entries]) => {
          const averages = {};
          [3, 5, 8].forEach(days => {
            const lastDays = getLastNDates(days);
            const filtered = entries.filter(e => lastDays.some(d => formatDate(e.date) === formatDate(d)));
            if (filtered.length > 0) {
              averages[days] = {
                sleep: average(filtered.map(e => e.sleep)),
                sore: average(filtered.map(e => e.sore)),
                fatigue: average(filtered.map(e => e.fatigue))
              };
            } else {
              averages[days] = null;
            }
          });
          newRolling[name] = averages;
        });
        setRollingAverages(newRolling);
      });
  }, []);

  const parseDate = (str) => {
    const [d, m, y] = str.split(" ")[0].split("/");
    return new Date(`${y}-${m}-${d}`);
  };

  const formatDate = (date) => date.toISOString().split('T')[0];

  const average = (arr) => arr.length ? (arr.reduce((a, b) => a + b, 0) / arr.length).toFixed(2) : ' 转  拽砖';

  const getLastNDates = (n) => {
    const dates = [];
    for (let i = 0; i < n; i++) {
      const d = new Date();
      d.setDate(d.getDate() - i);
      dates.push(new Date(d.getFullYear(), d.getMonth(), d.getDate()));
    }
    return dates;
  };

  const handleRangeAverage = () => {
    const fromDate = new Date(dateRange.from);
    const toDate = new Date(dateRange.to);
    const filtered = data.filter(e => e.date >= fromDate && e.date <= toDate);
    setRangeAverage({
      sleep: average(filtered.map(e => e.sleep)),
      sore: average(filtered.map(e => e.sore)),
      fatigue: average(filtered.map(e => e.fatigue))
    });
  };

  return (
    <div className="wellness-report" dir="rtl" style={{ padding: '20px' }}>
      <h1> Wellness</h1>

      <h2>爪注 拽爪转 - 砖  专</h2>
      <table border="1" cellPadding="8">
        <thead>
          <tr><th>转专</th><th>砖 </th><th>注驻转 </th><th> 砖专专 </th></tr>
        </thead>
        <tbody>
          {dailyGroupAverages.map((row, i) => (
            <tr key={i}>
              <td>{row.date}</td>
              <td>{row.sleep}</td>
              <td>{row.fatigue}</td>
              <td>{row.sore}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>爪注 拽爪转 驻  转专</h2>
      <input type="date" onChange={(e) => setDateRange(r => ({ ...r, from: e.target.value }))} />
      <input type="date" onChange={(e) => setDateRange(r => ({ ...r, to: e.target.value }))} />
      <button onClick={handleRangeAverage}>砖 爪注</button>
      {rangeAverage && (
        <table border="1" cellPadding="8" style={{ marginTop: '10px' }}>
          <thead>
            <tr><th>砖 </th><th>注驻转 </th><th> 砖专专 </th></tr>
          </thead>
          <tbody>
            <tr>
              <td>{rangeAverage.sleep}</td>
              <td>{rangeAverage.fatigue}</td>
              <td>{rangeAverage.sore}</td>
            </tr>
          </tbody>
        </table>
      )}

      <h2>爪注 驻 砖拽转</h2>
      {playerNames.map(name => (
        <div key={name}>
          <button onClick={() => setExpandedPlayers(p => ({ ...p, [name]: !p[name] }))}>{expandedPlayers[name] ? '住转专' : '爪'} {name}</button>
          {expandedPlayers[name] && (
            <div>
              <h4>{name}</h4>
              {[3, 5, 8].map(days => (
                <p key={days}>
                  爪注 专 {days} : 
                  {rollingAverages[name]?.[days] ? ` 砖 ${rollingAverages[name][days].sleep}, 注驻转 ${rollingAverages[name][days].fatigue}, 砖专专 ${rollingAverages[name][days].sore}` : '  转  拽砖'}
                </p>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default WellnessReport;
