import React, { useEffect, useState } from 'react';

function WellnessReport() {
  const [averages, setAverages] = useState({});
  const sheetUrl = "https://opensheet.elk.sh/1x1e53gsx3VzEJ5ON5QOvYeJTGI8auMtSxJYYpUUmreY/Wallnes";

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(sheetUrl);
        const json = await response.json();

        const playerStats = {};
        json.forEach(entry => {
          const name = entry["Name"];
          const sleep = Number(entry["What is the quality of your sleep at night?"]);
          const sore = Number(entry["How sore are you?"]);
          const fatigue = Number(entry["How Fatigued are you?"]);

          if (!playerStats[name]) {
            playerStats[name] = { count: 0, sleep: 0, sore: 0, fatigue: 0 };
          }

          playerStats[name].count += 1;
          playerStats[name].sleep += sleep;
          playerStats[name].sore += sore;
          playerStats[name].fatigue += fatigue;
        });

        const result = {};
        for (const name in playerStats) {
          const stats = playerStats[name];
          result[name] = {
            sleep: (stats.sleep / stats.count).toFixed(2),
            sore: (stats.sore / stats.count).toFixed(2),
            fatigue: (stats.fatigue / stats.count).toFixed(2),
          };
        }

        setAverages(result);
      } catch (error) {
        console.error("שגיאה בטעינת הנתונים:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="wellness-report" dir="rtl" style={{ padding: '20px' }}>
      <h1>דוח Wellness</h1>

      {Object.keys(averages).length === 0 ? (
        <p>טוען נתונים...</p>
      ) : (
        <table border="1" cellPadding="8" style={{ borderCollapse: "collapse", width: "100%", textAlign: "center" }}>
          <thead>
            <tr>
              <th>שחקנית</th>
              <th>ממוצע שינה 💤</th>
              <th>ממוצע עייפות 😴</th>
              <th>ממוצע כאבי שרירים 💪</th>
            </tr>
          </thead>
          <tbody>
            {Object.keys(averages).map(name => (
              <tr key={name}>
                <td>{name}</td>
                <td>{averages[name].sleep}</td>
                <td>{averages[name].fatigue}</td>
                <td>{averages[name].sore}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default WellnessReport;
