// ExerciseLibrary.js

import React from 'react';
import frontSquatImg from './images/front-squat.jpg'; // תמונה מקומית

const exercises = [
  {
    name: 'Front Squat',
    category: 'Legs',
    image: frontSquatImg,
    tags: ['High Intensity', 'Rehab: Knee']
  },
  {
    name: 'Push-ups',
    category: 'Chest',
    image: 'https://media.giphy.com/media/l0MYt5jPR6QX5pnqM/giphy.gif',
    tags: ['Medium Intensity']
  },
  {
    name: 'Plank',
    category: 'Core',
    image: 'https://media.giphy.com/media/l0MYydaQz1PjVg3Y4/giphy.gif',
    tags: ['Low Intensity']
  },
  {
    name: 'Bicep Curls',
    category: 'Arms',
    image: 'https://media.giphy.com/media/l0MYoL4fJcR6pNWrK/giphy.gif',
    tags: ['Low Intensity']
  }
];

// קיבוץ לפי קטגוריה
const groupedExercises = exercises.reduce((acc, curr) => {
  if (!acc[curr.category]) acc[curr.category] = [];
  acc[curr.category].push(curr);
  return acc;
}, {});

export default function ExerciseLibrary() {
  return (
    <div className="exercise-library" dir="rtl" style={{ padding: '20px' }}>
      <h1>מאגר תרגילים</h1>
      <table border="1" cellPadding="10" cellSpacing="0" style={{ width: '100%', textAlign: 'center' }} dir="rtl">
        <thead>
          <tr style={{ backgroundColor: '#f0f0f0' }}>
            <th>Muscle Group</th>
            <th>Exercises</th>
          </tr>
        </thead>
        <tbody>
          {Object.entries(groupedExercises).map(([category, exList], idx) => (
            <tr key={idx}>
              <td style={{ fontWeight: 'bold', backgroundColor: '#f5f5f5' }}>{category}</td>
              <td>
                <div style={{ display: 'flex', gap: '40px', justifyContent: 'flex-start', flexWrap: 'wrap' }}>
                  {exList.map((ex, i) => (
                    <div key={i} style={{ width: '160px' }}>
                      <p style={{ fontWeight: 'bold' }}>{ex.name}</p>
                      <img src={ex.image} alt={ex.name} style={{ width: '100%', height: 'auto' }} />
                    </div>
                  ))}
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
