// --- GymCourtReport.js ---
import React, { useState, useEffect } from 'react';
import dayjs from 'dayjs';
import 'dayjs/locale/he';

dayjs.locale('he');

const SHEET_BASE = "https://opensheet.elk.sh/1x1e53gsx3VzEJ5ON5QOvYeJTGI8auMtSxJYYpUUmreY";

const formatDate = (dateStr) => dayjs(dateStr, 'DD/MM/YYYY HH:mm:ss').format('YYYY-MM-DD');

function processRPEData(data, fieldName) {
  const daily = {};
  const playerDaily = {};

  data.forEach(entry => {
    const date = formatDate(entry.Timestamp).slice(0, 10);
    const name = entry.Name;
    const rpe = Number(entry[fieldName]);

    if (!rpe || !name || isNaN(rpe)) return;

    if (!daily[date]) daily[date] = [];
    daily[date].push(rpe);

    if (!playerDaily[name]) playerDaily[name] = {};
    if (!playerDaily[name][date]) playerDaily[name][date] = [];
    playerDaily[name][date].push(rpe);
  });

  const dailyAvg = Object.entries(daily).map(([date, values]) => ({
    date,
    avg: (values.reduce((a, b) => a + b, 0) / values.length).toFixed(1)
  })).sort((a, b) => new Date(a.date) - new Date(b.date));

  return { dailyAvg, playerDaily };
}

function RPEBlock({ title, url, field }) {
  const [data, setData] = useState([]);
  const [processed, setProcessed] = useState(null);
  const [selectedPlayer, setSelectedPlayer] = useState(null);

  useEffect(() => {
    fetch(url)
      .then(res => res.json())
      .then(json => {
        setData(json);
        setProcessed(processRPEData(json, field));
      });
  }, [url, field]);

  const getRollingAvg = (days) => {
    if (!processed) return 0;
    const recent = processed.dailyAvg.slice(-days);
    const values = recent.map(d => Number(d.avg));
    return values.length ? (values.reduce((a, b) => a + b, 0) / values.length).toFixed(1) : '◊ê◊ô◊ü ◊†◊™◊ï◊†◊ô◊ù';
  };

  const getCustomAvg = (from, to) => {
    if (!processed) return 0;
    const filtered = processed.dailyAvg.filter(d => d.date >= from && d.date <= to);
    const values = filtered.map(d => Number(d.avg));
    return values.length ? (values.reduce((a, b) => a + b, 0) / values.length).toFixed(1) : '◊ê◊ô◊ü ◊†◊™◊ï◊†◊ô◊ù';
  };

  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [customAvg, setCustomAvg] = useState(null);

  return (
    <div style={{ marginBottom: '40px' }}>
      <h2>{title}</h2>

      <h3>◊û◊û◊ï◊¶◊¢ ◊ú◊§◊ô ◊ô◊û◊ô◊ù (8 ◊ô◊û◊ô◊ù ◊ê◊ó◊®◊ï◊†◊ô◊ù)</h3>
      <table border="1" cellPadding="6">
        <thead>
          <tr><th>◊™◊ê◊®◊ô◊ö</th><th>◊û◊û◊ï◊¶◊¢ RPE</th></tr>
        </thead>
        <tbody>
          {processed && processed.dailyAvg.slice(-8).map((entry, i) => (
            <tr key={i}><td>{entry.date}</td><td>{entry.avg}</td></tr>
          ))}
        </tbody>
      </table>

      <h3>◊û◊û◊ï◊¶◊¢ ◊û◊™◊í◊ú◊í◊ú</h3>
      <p>3 ◊ô◊û◊ô◊ù: {getRollingAvg(3)} | 5 ◊ô◊û◊ô◊ù: {getRollingAvg(5)} | 8 ◊ô◊û◊ô◊ù: {getRollingAvg(8)}</p>

      <h3>◊û◊û◊ï◊¶◊¢ ◊ú◊§◊ô ◊ò◊ï◊ï◊ó ◊™◊ê◊®◊ô◊õ◊ô◊ù</h3>
      <input type="date" value={from} onChange={e => setFrom(e.target.value)} />
      <input type="date" value={to} onChange={e => setTo(e.target.value)} />
      <button onClick={() => setCustomAvg(getCustomAvg(from, to))}>◊ó◊©◊ë ◊û◊û◊ï◊¶◊¢</button>
      {customAvg && <p>◊û◊û◊ï◊¶◊¢ ◊ë◊ò◊ï◊ï◊ó: {customAvg}</p>}

      <h3>◊û◊û◊ï◊¶◊¢◊ô◊ù ◊ú◊§◊ô ◊©◊ó◊ß◊†◊ô◊™</h3>
      {processed && Object.keys(processed.playerDaily).map((name, idx) => (
        <div key={idx} style={{ margin: '10px 0' }}>
          <button onClick={() => setSelectedPlayer(selectedPlayer === name ? null : name)}>
            {selectedPlayer === name ? 'üîΩ' : '‚ñ∂Ô∏è'} {name}
          </button>
          {selectedPlayer === name && (
            <table border="1" cellPadding="6">
              <thead><tr><th>◊™◊ê◊®◊ô◊ö</th><th>RPE</th></tr></thead>
              <tbody>
                {Object.entries(processed.playerDaily[name])
                  .sort((a, b) => new Date(a[0]) - new Date(b[0]))
                  .slice(-8)
                  .map(([date, values], i) => (
                    <tr key={i}><td>{date}</td><td>{(values.reduce((a, b) => a + b, 0) / values.length).toFixed(1)}</td></tr>
                  ))}
              </tbody>
            </table>
          )}
        </div>
      ))}
    </div>
  );
}

export default function GymCourtReport() {
  return (
    <div className="report" dir="rtl" style={{ padding: '20px' }}>
      <h1>◊ì◊ï"◊ó RPE - ◊ó◊ì◊® ◊õ◊ï◊©◊® ◊ï◊û◊í◊®◊©</h1>
      <RPEBlock title="üèãÔ∏è ◊ó◊ì◊® ◊õ◊ï◊©◊® (Gym)" url={`${SHEET_BASE}/Gym`} field="RPE ◊ë◊ó◊ì◊® ◊õ◊ï◊©◊®" />
      <RPEBlock title="üèÄ ◊û◊í◊®◊© (Court)" url={`${SHEET_BASE}/Court`} field="RPE ◊ë◊û◊í◊®◊©" />
    </div>
  )
}
