// src/GymCourtReport.js
import React, { useEffect, useState } from 'react';
import dayjs from 'dayjs';
import 'dayjs/locale/he';

dayjs.locale('he');
const SHEET_BASE = "https://opensheet.elk.sh/1x1e53gsx3VzEJ5ON5QOvYeJTGI8auMtSxJYYpUUmreY";

const formatDate = (timestamp) => {
  const date = dayjs(timestamp, "DD/MM/YYYY HH:mm:ss");
  return date.isValid() ? date.format("YYYY-MM-DD") : null;
};

const calculateRollingAverage = (arr, days) => {
  const sliced = arr.slice(-days).filter(item => !isNaN(item.avg));
  if (sliced.length === 0) return '◊ê◊ô◊ü ◊†◊™◊ï◊†◊ô◊ù';
  const sum = sliced.reduce((acc, item) => acc + item.avg, 0);
  return (sum / sliced.length).toFixed(1);
};

const calculateCustomAverage = (data, from, to) => {
  const filtered = data.filter(entry => entry.date >= from && entry.date <= to && !isNaN(entry.avg));
  if (!filtered.length) return '◊ê◊ô◊ü ◊†◊™◊ï◊†◊ô◊ù';
  const sum = filtered.reduce((acc, item) => acc + item.avg, 0);
  return (sum / filtered.length).toFixed(1);
};

function RPEBlock({ title, tabName, field }) {
  const [rawData, setRawData] = useState([]);
  const [dailyAverages, setDailyAverages] = useState([]);
  const [playersData, setPlayersData] = useState({});
  const [selectedPlayer, setSelectedPlayer] = useState(null);
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [customAvg, setCustomAvg] = useState(null);

  useEffect(() => {
    fetch(`${SHEET_BASE}/${tabName}`)
      .then(res => res.json())
      .then(json => {
        const groupedByDate = {};
        const groupedByPlayer = {};

        json.forEach(entry => {
          const name = entry["Name"];
          const date = formatDate(entry["Timestamp"]);
          const rpe = Number(entry[field]);

          if (!name || !date || isNaN(rpe)) return;

          if (!groupedByDate[date]) groupedByDate[date] = [];
          groupedByDate[date].push(rpe);

          if (!groupedByPlayer[name]) groupedByPlayer[name] = {};
          if (!groupedByPlayer[name][date]) groupedByPlayer[name][date] = [];
          groupedByPlayer[name][date].push(rpe);
        });

        const dailyAvg = Object.entries(groupedByDate)
          .map(([date, values]) => ({ date, avg: values.reduce((a, b) => a + b, 0) / values.length }))
          .sort((a, b) => new Date(a.date) - new Date(b.date));

        setRawData(json);
        setDailyAverages(dailyAvg);
        setPlayersData(groupedByPlayer);
      });
  }, [tabName, field]);

  return (
    <div style={{ marginBottom: '40px' }}>
      <h2>{title}</h2>

      <h3>◊û◊û◊ï◊¶◊¢ ◊ú◊§◊ô ◊ô◊û◊ô◊ù (8 ◊ô◊û◊ô◊ù ◊ê◊ó◊®◊ï◊†◊ô◊ù)</h3>
      <table border="1" cellPadding="6">
        <thead>
          <tr><th>◊™◊ê◊®◊ô◊ö</th><th>◊û◊û◊ï◊¶◊¢ RPE</th></tr>
        </thead>
        <tbody>
          {dailyAverages.slice(-8).map((entry, i) => (
            <tr key={i}><td>{entry.date}</td><td>{entry.avg.toFixed(1)}</td></tr>
          ))}
        </tbody>
      </table>

      <h3>◊û◊û◊ï◊¶◊¢◊ô◊ù ◊û◊™◊í◊ú◊í◊ú◊ô◊ù ◊ß◊ë◊ï◊¶◊™◊ô◊ô◊ù</h3>
      <p>3 ◊ô◊û◊ô◊ù: {calculateRollingAverage(dailyAverages, 3)} | 5 ◊ô◊û◊ô◊ù: {calculateRollingAverage(dailyAverages, 5)} | 8 ◊ô◊û◊ô◊ù: {calculateRollingAverage(dailyAverages, 8)}</p>

      <h3>◊û◊û◊ï◊¶◊¢ ◊ú◊§◊ô ◊ò◊ï◊ï◊ó ◊™◊ê◊®◊ô◊õ◊ô◊ù</h3>
      <input type="date" value={from} onChange={(e) => setFrom(e.target.value)} />
      <input type="date" value={to} onChange={(e) => setTo(e.target.value)} />
      <button onClick={() => setCustomAvg(calculateCustomAverage(dailyAverages, from, to))}>◊ó◊©◊ë ◊û◊û◊ï◊¶◊¢</button>
      {customAvg && <p>◊û◊û◊ï◊¶◊¢ ◊ë◊ò◊ï◊ï◊ó: {customAvg}</p>}

      <h3>◊û◊û◊ï◊¶◊¢◊ô◊ù ◊ú◊§◊ô ◊©◊ó◊ß◊†◊ô◊™</h3>
      {Object.entries(playersData).map(([name, dates]) => {
        const playerAverages = Object.entries(dates).map(([date, values]) => ({
          date,
          avg: values.reduce((a, b) => a + b, 0) / values.length
        })).sort((a, b) => new Date(a.date) - new Date(b.date));

        const rollingAvg = (days) => {
          const sliced = playerAverages.slice(-days).filter(e => !isNaN(e.avg));
          if (!sliced.length) return '◊ê◊ô◊ü ◊†◊™◊ï◊†◊ô◊ù';
          const sum = sliced.reduce((acc, item) => acc + item.avg, 0);
          return (sum / sliced.length).toFixed(1);
        };

        const customPlayerAvg = (fromDate, toDate) => {
          const filtered = playerAverages.filter(e => e.date >= fromDate && e.date <= toDate && !isNaN(e.avg));
          if (!filtered.length) return '◊ê◊ô◊ü ◊†◊™◊ï◊†◊ô◊ù';
          const sum = filtered.reduce((acc, item) => acc + item.avg, 0);
          return (sum / filtered.length).toFixed(1);
        };

        return (
          <div key={name} style={{ marginBottom: '20px' }}>
            <button onClick={() => setSelectedPlayer(selectedPlayer === name ? null : name)}>
              {selectedPlayer === name ? 'üîΩ' : '‚ñ∂Ô∏è'} {name}
            </button>
            {selectedPlayer === name && (
              <div style={{ marginTop: '10px' }}>
                <table border="1" cellPadding="6">
                  <thead><tr><th>◊™◊ê◊®◊ô◊ö</th><th>RPE</th></tr></thead>
                  <tbody>
                    {playerAverages.slice(-8).map((entry, i) => (
                      <tr key={i}><td>{entry.date}</td><td>{entry.avg.toFixed(1)}</td></tr>
                    ))}
                  </tbody>
                </table>

                <p>3 ◊ô◊û◊ô◊ù: {rollingAvg(3)} | 5 ◊ô◊û◊ô◊ù: {rollingAvg(5)} | 8 ◊ô◊û◊ô◊ù: {rollingAvg(8)}</p>

                <div style={{ marginTop: '10px' }}>
                  <input type="date" onChange={(e) => setFrom(e.target.value)} />
                  <input type="date" onChange={(e) => setTo(e.target.value)} />
                  <button onClick={() => setCustomAvg(customPlayerAvg(from, to))}>◊ó◊©◊ë ◊û◊û◊ï◊¶◊¢ ◊ê◊ô◊©◊ô</button>
