import React, { useEffect, useState } from 'react';

function WellnessReport() {
  const [data, setData] = useState([]);
  const [groupDailyAverages, setGroupDailyAverages] = useState([]);
  const [groupRollingAverages, setGroupRollingAverages] = useState({});
  const [groupRangeAverage, setGroupRangeAverage] = useState(null);
  const [players, setPlayers] = useState([]);
  const [dateRange, setDateRange] = useState({ from: '', to: '' });

  const sheetUrl = "https://opensheet.elk.sh/1x1e53gsx3VzEJ5ON5QOvYeJTGI8auMtSxJYYpUUmreY/Wallnes";

  useEffect(() => {
    fetch(sheetUrl)
      .then(res => res.json())
      .then(json => {
        setData(json);
        const playerSet = new Set(json.map(row => row.Name));
        setPlayers(Array.from(playerSet));

        const grouped = {};
        json.forEach(row => {
          const dateStr = row.Timestamp.split(' ')[0];
          if (!grouped[dateStr]) grouped[dateStr] = [];
          grouped[dateStr].push(row);
        });

        const sortedDates = Object.keys(grouped).sort((a, b) => new Date(a.split('/').reverse().join('/')) - new Date(b.split('/').reverse().join('/')));
        const last8Dates = sortedDates.slice(-8);

        const daily = last8Dates.map(date => {
          const rows = grouped[date];
          const count = rows.length;
          const sleep = rows.reduce((sum, r) => sum + Number(r["What is the quality of your sleep at night?"] || 0), 0) / count;
          const fatigue = rows.reduce((sum, r) => sum + Number(r["How Fatigued are you?"] || 0), 0) / count;
          const sore = rows.reduce((sum, r) => sum + Number(r["How sore are you?"] || 0), 0) / count;
          return { date, sleep: sleep.toFixed(1), fatigue: fatigue.toFixed(1), sore: sore.toFixed(1) };
        });

        setGroupDailyAverages(daily);

        const rolling = { sleep: 0, fatigue: 0, sore: 0, count: 0 };
        daily.forEach(day => {
          rolling.sleep += parseFloat(day.sleep);
          rolling.fatigue += parseFloat(day.fatigue);
          rolling.sore += parseFloat(day.sore);
          rolling.count++;
        });

        if (rolling.count > 0) {
          setGroupRollingAverages({
            sleep: (rolling.sleep / rolling.count).toFixed(1),
            fatigue: (rolling.fatigue / rolling.count).toFixed(1),
            sore: (rolling.sore / rolling.count).toFixed(1)
          });
        }
      });
  }, []);

  const handleRangeAverage = () => {
    if (!dateRange.from || !dateRange.to) return;

    const from = new Date(dateRange.from);
    const to = new Date(dateRange.to);

    const filtered = data.filter(row => {
      const date = new Date(row.Timestamp.split(' ')[0].split('/').reverse().join('/'));
      return date >= from && date <= to;
    });

    const count = filtered.length;
    if (count === 0) {
      setGroupRangeAverage(' 转  转专');
      return;
    }

    const sleep = filtered.reduce((sum, r) => sum + Number(r["What is the quality of your sleep at night?"] || 0), 0) / count;
    const fatigue = filtered.reduce((sum, r) => sum + Number(r["How Fatigued are you?"] || 0), 0) / count;
    const sore = filtered.reduce((sum, r) => sum + Number(r["How sore are you?"] || 0), 0) / count;

    setGroupRangeAverage({
      sleep: sleep.toFixed(1),
      fatigue: fatigue.toFixed(1),
      sore: sore.toFixed(1)
    });
  };

  const getRecentAverage = (filteredDates, daysBack) => {
    const today = new Date();
    const recentDates = [];
    let counter = 0;
    for (let i = filteredDates.length - 1; i >= 0 && counter < daysBack; i--) {
      recentDates.push(filteredDates[i]);
      counter++;
    }
    return recentDates;
  };

  return (
    <div dir="rtl" style={{ padding: '20px' }}>
      <h1> Wellness</h1>

      <h2>爪注 拽爪转 - 驻  (8  专)</h2>
      <table border="1" cellPadding="8">
        <thead>
          <tr>
            <th>转专</th>
            <th>砖 </th>
            <th>注驻转 </th>
            <th> 砖专专 </th>
          </tr>
        </thead>
        <tbody>
          {groupDailyAverages.map((row, i) => (
            <tr key={i}>
              <td>{row.date}</td>
              <td>{row.sleep}</td>
              <td>{row.fatigue}</td>
              <td>{row.sore}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>爪注 拽爪转 转 - 8  专</h2>
      {groupRollingAverages.sleep ? (
        <p>
          砖: {groupRollingAverages.sleep} | 注驻转: {groupRollingAverages.fatigue} |  砖专专: {groupRollingAverages.sore}
        </p>
      ) : (
        <p> 转 爪注 转</p>
      )}

      <h2>爪注 拽爪转 驻  转专</h2>
      <div>
        <label>转专: <input type="date" value={dateRange.from} onChange={e => setDateRange({ ...dateRange, from: e.target.value })} /></label>
        <label>注 转专: <input type="date" value={dateRange.to} onChange={e => setDateRange({ ...dateRange, to: e.target.value })} /></label>
        <button onClick={handleRangeAverage}>砖 爪注</button>
      </div>
      {groupRangeAverage && (
        typeof groupRangeAverage === 'string' ? <p>{groupRangeAverage}</p> :
          <p>
            砖: {groupRangeAverage.sleep} | 注驻转: {groupRangeAverage.fatigue} |  砖专专: {groupRangeAverage.sore}
          </p>
      )}
    </div>
  );
}

export default WellnessReport;
