import React, { useState } from 'react';

const exercises = [
  { name: "Squat", category: "Legs", image: "https://media.giphy.com/media/l41YxHD9D2Sa3sFXy/giphy.gif" },
  { name: "Lunges", category: "Legs", image: "https://media.giphy.com/media/3oKIPwoeGErMmaI43C/giphy.gif" },
  { name: "Push-ups", category: "Chest", image: "https://media.giphy.com/media/3o7TKtd1CdqkJuDfa8/giphy.gif" },
  { name: "Plank", category: "Core", image: "https://media.giphy.com/media/UoLt6Tm8wlSnWGfSFs/giphy.gif" },
  { name: "Bicep Curls", category: "Arms", image: "https://media.giphy.com/media/hT5pR50UVmZ7v4QxVO/giphy.gif" }
];

const rehabProtocols = {
  "Knee Rehab": ["Squat", "Lunges"],
  "Shoulder Rehab": ["Push-ups", "Bicep Curls"]
};

const categories = [...new Set(exercises.map(e => e.category))];

function ExerciseLibrary() {
  const [selectedProtocol, setSelectedProtocol] = useState("");

  const filteredExercises = selectedProtocol
    ? exercises.filter(ex => rehabProtocols[selectedProtocol]?.includes(ex.name))
    : exercises;

  return (
    <div style={{ padding: '20px' }} dir="rtl">
      <h1>Exercise Library</h1>

      <label>
        :Rehabilitation Protocol
        <select value={selectedProtocol} onChange={(e) => setSelectedProtocol(e.target.value)} style={{ marginRight: '10px' }}>
          <option value="">-- Select Protocol --</option>
          {Object.keys(rehabProtocols).map((protocol, idx) => (
            <option key={idx} value={protocol}>{protocol}</option>
          ))}
        </select>
      </label>

      <table border="1" cellPadding="10" style={{ width: '100%', marginTop: '20px' }} dir="rtl">
        <thead>
          <tr>
            <th style={{ backgroundColor: '#eee' }}>Muscle Group</th>
            <th colSpan="4">Exercises</th>
          </tr>
        </thead>
        <tbody>
          {categories.map((cat, i) => {
            const categoryExercises = filteredExercises.filter(e => e.category === cat);
            return (
              <tr key={i}>
                <td style={{ backgroundColor: '#f3f3f3', fontWeight: 'bold', textAlign: 'center' }}>{cat}</td>
                {categoryExercises.map((ex, j) => (
                  <td key={j} style={{ textAlign: 'center' }}>
                    <div>{ex.name}</div>
                    <img src={ex.image} alt={ex.name} style={{ width: '100px', height: '100px' }} />
                  </td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default ExerciseLibrary;
