import React, { useEffect, useState } from 'react';

function WellnessReport() {
  const [data, setData] = useState([]);
  const [playerNames, setPlayerNames] = useState([]);
  const [groupAverages, setGroupAverages] = useState({});
  const [dateRange, setDateRange] = useState({ from: '', to: '' });
  const [filteredGroupAverage, setFilteredGroupAverage] = useState(null);
  const [expandedPlayers, setExpandedPlayers] = useState({});

  const sheetUrl = "https://opensheet.elk.sh/1x1e53gsx3VzEJ5ON5QOvYeJTGI8auMtSxJYYpUUmreY/Wallnes";

  useEffect(() => {
    fetch(sheetUrl)
      .then(res => res.json())
      .then(json => {
        const processed = json.map(entry => ({
          name: entry["Name"],
          sleep: Number(entry["What is the quality of your sleep at night?"]),
          sore: Number(entry["How sore are you?"]),
          fatigue: Number(entry["How Fatigued are you?"]),
          date: new Date(entry.Timestamp.split(" ")[0].split("/").reverse().join("-"))
        })).filter(entry => !isNaN(entry.date));

        setData(processed);

        const names = [...new Set(processed.map(d => d.name))];
        setPlayerNames(names);

        const last5Days = getLastNDates(5);
        const recent = processed.filter(d => last5Days.some(date => sameDate(d.date, date)));
        setGroupAverages(calculateAverages(recent));
      });
  }, []);

  const getLastNDates = (n) => {
    const today = new Date();
    return Array.from({ length: n }, (_, i) => {
      const d = new Date();
      d.setDate(today.getDate() - i);
      return new Date(d.getFullYear(), d.getMonth(), d.getDate());
    });
  };

  const sameDate = (d1, d2) => {
    return d1.getFullYear() === d2.getFullYear() &&
      d1.getMonth() === d2.getMonth() &&
      d1.getDate() === d2.getDate();
  };

  const calculateAverages = (entries) => {
    const total = { sleep: 0, sore: 0, fatigue: 0 };
    let count = 0;

    entries.forEach(e => {
      if (!isNaN(e.sleep) && !isNaN(e.sore) && !isNaN(e.fatigue)) {
        total.sleep += e.sleep;
        total.sore += e.sore;
        total.fatigue += e.fatigue;
        count++;
      }
    });

    return count > 0 ? {
      sleep: (total.sleep / count).toFixed(2),
      sore: (total.sore / count).toFixed(2),
      fatigue: (total.fatigue / count).toFixed(2),
    } : null;
  };

  const handleRangeChange = (e) => {
    const { name, value } = e.target;
    setDateRange(prev => ({ ...prev, [name]: value }));
  };

  const calculateGroupAverageForRange = () => {
    const from = new Date(dateRange.from);
    const to = new Date(dateRange.to);
    const filtered = data.filter(d => d.date >= from && d.date <= to);
    setFilteredGroupAverage(calculateAverages(filtered));
  };

  const togglePlayer = (name) => {
    setExpandedPlayers(prev => ({ ...prev, [name]: !prev[name] }));
  };

  const playerAveragesByDay = (name) => {
    const playerData = data.filter(d => d.name === name);
    const grouped = {};

    playerData.forEach(d => {
      const key = d.date.toISOString().split('T')[0];
      if (!grouped[key]) grouped[key] = [];
      grouped[key].push(d);
    });

    const rows = Object.entries(grouped).map(([date, values]) => {
      const avg = calculateAverages(values);
      return { date, ...avg };
    });

    const last3 = rows.slice(-3);
    const last5 = rows.slice(-5);
    const last8 = rows.slice(-8);

    const extra = {
      last3: calculateAverages(last3),
      last5: calculateAverages(last5),
      last8: calculateAverages(last8),
    };

    return { rows, extra };
  };

  return (
    <div dir="rtl" style={{ padding: 20 }}>
      <h1>דוח Wellness</h1>

      <h2>ממוצע קבוצתי - חמישה ימים אחרונים</h2>
      {groupAverages && (
        <table border="1" cellPadding="6">
          <thead>
            <tr><th>שינה 💤</th><th>עייפות 😴</th><th>כאבי שרירים 💪</th></tr>
          </thead>
          <tbody>
            <tr>
              <td>{groupAverages.sleep}</td>
              <td>{groupAverages.fatigue}</td>
              <td>{groupAverages.sore}</td>
            </tr>
          </tbody>
        </table>
      )}

      <h2 style={{ marginTop: 40 }}>ממוצע קבוצתי לפי טווח תאריכים</h2>
      <div>
        <input type="date" name="from" onChange={handleRangeChange} />
        <input type="date" name="to" onChange={handleRangeChange} />
        <button onClick={calculateGroupAverageForRange}>חשב ממוצע</button>
      </div>
      {filteredGroupAverage && (
        <table border="1" cellPadding="6" style={{ marginTop: 10 }}>
          <thead>
            <tr><th>שינה 💤</th><th>עייפות 😴</th><th>כאבי שרירים 💪</th></tr>
          </thead>
          <tbody>
            <tr>
              <td>{filteredGroupAverage.sleep}</td>
              <td>{filteredGroupAverage.fatigue}</td>
              <td>{filteredGroupAverage.sore}</td>
            </tr>
          </tbody>
        </table>
      )}

      <h2 style={{ marginTop: 40 }}>ממוצעים לפי שחקנית</h2>
      {playerNames.map(name => (
        <div key={name} style={{ marginBottom: 30 }}>
          <button onClick={() => togglePlayer(name)}>{expandedPlayers[name] ? 'הסתר' : 'הצג'} נתונים עבור {name}</button>
          {expandedPlayers[name] && (() => {
            const { rows, extra } = playerAveragesByDay(name);
            return (
              <>
                <table border="1" cellPadding="6" style={{ marginTop: 10 }}>
                  <thead>
                    <tr><th>תאריך</th><th>שינה</th><th>עייפות</th><th>כאבי שרירים</th></tr>
                  </thead>
                  <tbody>
                    {rows.map((r, idx) => (
                      <tr key={idx}>
                        <td>{r.date}</td>
                        <td>{r.sleep}</td>
                        <td>{r.fatigue}</td>
                        <td>{r.sore}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
                <p style={{ marginTop: 10 }}>ממוצע 3 ימים אחרונים: 💤 {extra.last3.sleep}, 😴 {extra.last3.fatigue}, 💪 {extra.last3.sore}</p>
                <p>ממוצע 5 ימים אחרונים: 💤 {extra.last5.sleep}, 😴 {extra.last5.fatigue}, 💪 {extra.last5.sore}</p>
                <p>ממוצע 8 ימים אחרונים: 💤 {extra.last8.sleep}, 😴 {extra.last8.fatigue}, 💪 {extra.last8.sore}</p>
              </>
            );
          })()}
        </div>
      ))}
    </div>
  );
}

export default WellnessReport;
