import React, { useState, useEffect } from 'react';
import './App.css';

const defaultPlayers = [
  { name: "×©×™×¨×”", active: true },
  { name: "× ×•×¢×”", active: true },
  { name: "×“× ×”", active: true },
  { name: "×¨×•×ª", active: true },
  { name: "×”×™×œ×”", active: true }
];

const getSavedPlayers = () => {
  const saved = localStorage.getItem("players");
  return saved ? JSON.parse(saved) : defaultPlayers;
};

function formatTime(sec) {
  const minutes = String(Math.floor(sec / 60)).padStart(2, '0');
  const seconds = String(sec % 60).padStart(2, '0');
  return `${minutes}:${seconds}`;
}

function MainGame() {
  const [quarter, setQuarter] = useState(1);
  const [timeLeft, setTimeLeft] = useState(600);
  const [totalGameTime, setTotalGameTime] = useState(0);
  const [running, setRunning] = useState(false);
  const [showHistory, setShowHistory] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [editOpponentsMode, setEditOpponentsMode] = useState(false);
  const [timeoutMode, setTimeoutMode] = useState(false);
  const [breakMode, setBreakMode] = useState(false);
  const [editClockMode, setEditClockMode] = useState(false);
  const [editClockValue, setEditClockValue] = useState(600);
  const [timeoutStart, setTimeoutStart] = useState(null);

  const [players, setPlayers] = useState(getSavedPlayers().map(p => ({
    name: p.name,
    active: p.active,
    total: 0,
    startTime: null,
    restStart: 0,
    totalRest: 0,
    hideRest: false,
    fouls: 0,
    points: 0,
    history: [],
    starForTimeout: false
  })));

  const [opponents, setOpponents] = useState([]);
  useEffect(() => {
    let timer;
    if (running && timeLeft > 0 && !timeoutMode && !breakMode) {
      timer = setInterval(() => {
        setTimeLeft(prev => prev - 1);
        setTotalGameTime(prev => {
          const newTime = prev + 1;

          // ×¢×“×›×•×Ÿ ×–××Ÿ ×× ×•×—×” ×œ×©×—×§× ×™×•×ª ×¢×œ ×”×¡×¤×¡×œ
          setPlayers(players => players.map(p => {
            if (p.startTime === null && p.restStart !== 0 && !p.hideRest) {
              return {
                ...p,
                totalRest: p.totalRest + 1
              };
            }
            return p;
          }));

          return newTime;
        });
      }, 1000);
    }

    // ×¡×™×•× ×¨×‘×¢
    if (timeLeft === 0 && running) {
      setRunning(false);
      setBreakMode(true);
      alert(`×¡×™×•× ×¨×‘×¢ ${quarter}`);
      if (quarter < 4) {
        setQuarter(prev => prev + 1);
        setTimeLeft(600);
      }
    }

    return () => clearInterval(timer);
  }, [running, timeLeft, quarter, timeoutMode, breakMode]);
  const handleTimeout = () => {
    setRunning(false);
    setTimeoutMode(true);
    setTimeoutStart(totalGameTime);
  };

  const resumeFromTimeout = () => {
    const timeoutEnd = totalGameTime;

    setPlayers(players => players.map(p => {
      // ×× ×”×©×—×§× ×™×ª ×”×™×™×ª×” ×¢×œ ×”××’×¨×© ×œ××•×¨×š ×›×œ ×”×˜×™×™× ×××•×˜
      if (p.startTime !== null && timeoutStart !== null && timeoutEnd > timeoutStart) {
        return {
          ...p,
          starForTimeout: true
        };
      }
      return p;
    }));
    setTimeoutMode(false);
    setTimeoutStart(null);
    setRunning(true);
  };
  const startQuarter = () => {
    setRunning(true);
    setTimeoutMode(false);
    setBreakMode(false);
  };
  const stopQuarter = () => {
    setRunning(false);
  };
  const togglePlayer = (index) => {
    setPlayers(prev => {
      const updated = [...prev];
      const player = updated[index];
      const onCourt = player.startTime !== null;

      if (onCourt) {
        // ×”×©×—×§× ×™×ª ×™×•×¨×“×ª
        const now = totalGameTime;
        player.total += now - player.startTime;
        player.startTime = null;
        player.restStart = now;
        player.starForTimeout = false; // ××—×™×§×ª ×›×•×›×‘×™×ª ×‘×™×¨×™×“×”
        player.history.push({ action: 'out', time: now });
      } else {
        // ×”×©×—×§× ×™×ª × ×›× ×¡×ª
        const now = totalGameTime;
        player.startTime = now;
        player.restStart = 0;
        player.history.push({ action: 'in', time: now });
      }

      updated[index] = player;
      return updated;
    });
  };

  const toggleOpponent = (index) => {
    setOpponents(prev => {
      const updated = [...prev];
      const player = updated[index];
      const onCourt = player.startTime !== null;

      if (onCourt) {
        const now = totalGameTime;
        player.total += now - player.startTime;
        player.startTime = null;
        player.restStart = now;
      } else {
        const now = totalGameTime;
        player.startTime = now;
        player.restStart = 0;
      }

      updated[index] = player;
      return updated;
    });
  };

  const changeValue = (setFunc, index, key, amount) => {
    setFunc(prev => {
      const updated = [...prev];
      updated[index][key] += amount;
      if (updated[index][key] < 0) updated[index][key] = 0;
      return updated;
    });
  };

  const savePlayers = () => {
    localStorage.setItem("players", JSON.stringify(players));
    alert("×”×§×‘×•×¦×” × ×©××¨×”!");
  };

  const saveOpponents = () => {
    localStorage.setItem("opponents", JSON.stringify(opponents));
    alert("×§×‘×•×¦×ª ×”×™×¨×™×‘×” × ×©××¨×”!");
  };
  return (
    <div className="App" dir="rtl">
      <h1>×‘×“×™×§×ª ×“×§×•×ª ××©×—×§</h1>

      <div className="section">
        <h3>×©×œ×™×˜×” ×‘××©×—×§</h3>
        <button onClick={startQuarter}>×”×ª×—×œ</button>
        <button onClick={stopQuarter}>×¢×¦×•×¨</button>
        <button onClick={handleTimeout}>â± ×˜×™×™× ×××•×˜</button>
        <button onClick={() => { setBreakMode(true); setRunning(false); }}>ğŸ“£ ×”×¤×¡×§×ª ×¨×‘×¢</button>
        <button onClick={() => setEditClockMode(!editClockMode)}>â° ×¡× ×›×¨×•×Ÿ ×©×¢×•×Ÿ</button>
      </div>
      <div className="section">
      <h3>× ×™×•×•×˜</h3>
      <button onClick={() => { setShowHistory(false); setEditMode(false); setEditOpponentsMode(false); }}>×—×–×¨×” ×œ××©×—×§</button>
